targets.expr:
(sort_desc(
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_query_bytes:rate5m
    ) [$__range:1m]
  ) > 0) /
  (sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_billable_bytes_received_per_second
    ) [$__range:1m]
  ) > 0)
) * on(id) group_left(name) grafanacloud_logs_instance_info

sort_desc(
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_query_bytes:rate5m
    ) [$__range:1m]
  ) /
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_billable_bytes_received_per_second
    ) [$__range:1m]
  )
) * on(id) group_left(name) grafanacloud_logs_instance_info
