targets.expr:
sort_desc(
  max by(id, name) (
    60 * grafanacloud_instance_samples_per_second{org_id="${org_id}"}
    / grafanacloud_instance_active_series{org_id="${org_id}"}

    * on(id) group_left(name) grafanacloud_instance_info{org_id="${org_id}", name=~"${tenant:value}"}
  )
)

(sort_desc(
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_query_bytes:rate5m{org_id="${org_id}"}
    ) [$__range:1m]
  ) > 0) /
  (sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_billable_bytes_received_per_second{org_id="${org_id}"}
    ) [$__range:1m]
  ) > 0)
) * on(id) group_left(name) grafanacloud_logs_instance_info{org_id="${org_id}"}

sort_desc(
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_query_bytes:rate5m{org_id="${org_id}"}
    ) [$__range:1m]
  ) /
  sum_over_time(
    sum by (id) (
      grafanacloud_logs_instance_billable_bytes_received_per_second{org_id="${org_id}"}
    ) [$__range:1m]
  )
) * on(id) group_left(name) grafanacloud_logs_instance_info{org_id="${org_id}"}
